CREATE OR REPLACE FUNCTION unit_test.assert_equal(in_exp anyelement, in_got anyelement,in_op character varying) RETURNS booleanAS$body$BEGIN	IF in_exp IS DISTINCT FROM in_got THEN 			PERFORM unit_test.fail(			E'Failed assert_equal (' || in_op || E').\n'			|| E'expected string <' || COALESCE(in_exp::VARCHAR, '<NULL>') || E'>\n'			|| E'got string      <' || COALESCE(in_got::VARCHAR, '<NULL>') || E'>'					);		RETURN false;	END IF;		PERFORM unit_test.pass();		RETURN TRUE;END;$body$    LANGUAGE plpgsql;