CREATE OR REPLACE FUNCTION unit_test.assert_null(in_exp anyelement, in_op character varying) RETURNS booleanAS$body$BEGIN	IF in_exp IS NULL THEN		PERFORM unit_test.fail(			E'Failed assert_null (' || in_op || E').\n'			|| E'expected <NULL> but got <' || COALESCE(in_exp::VARCHAR, '<NULL>') || E'>\n'		);		RETURN false;	END IF;		PERFORM unit_test.pass();			RETURN true;END;$body$    LANGUAGE plpgsql;