CREATE OR REPLACE FUNCTION unit_test.assert_true(in_exp boolean, in_op character varying) RETURNS booleanAS$body$BEGIN	IF in_exp IS DISTINCT FROM TRUE THEN		PERFORM unit_test.fail(			E'Failed assert_true (' || in_op || E').\n'			|| E'expected true but got <' || COALESCE(in_exp::VARCHAR, '<NULL>') || E'>\n'		);		RETURN false;	END IF;		PERFORM unit_test.pass();			RETURN true;END;$body$    LANGUAGE plpgsql;