CREATE OR REPLACE FUNCTION unit_test.begin_test_case(IN in_test_suite character varying,IN in_test_case character varying) RETURNS voidAS$body$BEGIN	UPDATE unit_test.results SET		done = false	WHERE test_suite = in_test_suite AND test_case = in_test_case;			IF NOT FOUND THEN		INSERT INTO unit_test.results(test_suite,test_case,done) 		SELECT in_test_suite as test_suite,in_test_case as test_case,false as done;	END IF;END;$body$    LANGUAGE plpgsql;